    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
        // DATOS
        bean = new Item(Integer.parseInt(txtNumero.getText()));
        //PROCESO
        OperacionesService objOperaciones = new OperacionesService();
        objOperaciones.proceso(bean);
        objOperaciones.suma(bean);
        objOperaciones.tipoNumero(bean);
        //REPORTE
        txtFactorial.setText(Integer.toString(bean.getFactorial()));
        txtPrimo.setText(bean.getTipo());
        txtSuma.setText(Integer.toString(bean.getSuma()));
       
    }//GEN-LAST:event_btnCalcularActionPerformed

    private void btnLimpiarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLimpiarActionPerformed
        txtNumero.setText(null);
        txtFactorial.setText("");
        txtPrimo.setText(null);
        txtSuma.setText("");
    }//GEN-LAST:event_btnLimpiarActionPerformed

